import { NextResponse } from "next/server";
import { getDB, isCloudEnabled } from "@/lib/db/connection";
import { getObservabilitySnapshot } from "@/lib/observability";

export const runtime = "nodejs";
export const dynamic = "force-dynamic";

export async function GET(): Promise<NextResponse> {
  const snapshot = getObservabilitySnapshot();

  if (!isCloudEnabled()) {
    return NextResponse.json(
      {
        ready: true,
        mode: "degraded",
        reason: "cloud_disabled",
        timestamp: new Date().toISOString(),
        last_reconciliation_run_at: snapshot.lastReconciliationRunAt,
      },
      { status: 200 },
    );
  }

  try {
    const db = getDB();
    db.prepare("SELECT 1").get();
    return NextResponse.json(
      {
        ready: true,
        mode: "full",
        cloud_db: "ok",
        timestamp: new Date().toISOString(),
        last_reconciliation_run_at: snapshot.lastReconciliationRunAt,
      },
      { status: 200 },
    );
  } catch (err) {
    return NextResponse.json(
      {
        ready: false,
        mode: "degraded",
        cloud_db: "unreachable",
        timestamp: new Date().toISOString(),
        error: "cloud_db_unreachable",
      },
      { status: 503 },
    );
  }
}

import { NextRequest, NextResponse } from "next/server";
import { requireAuth, cloudErrorResponse, requireRole, auditLog } from "@/lib/cloud-auth";
import { createApiKey, listApiKeys } from "@/lib/cloud-db";
import { CreateApiKeySchema, parseBody } from "@/lib/cloud-schemas";
import { logger } from "@/lib/logger";

export const runtime = "nodejs";

export async function GET(req: NextRequest): Promise<NextResponse> {
  const ctx = await requireAuth(req);
  if (ctx instanceof NextResponse) return ctx;
  const keys = listApiKeys(ctx.tenantId).map((k) => ({
    id: k.id,
    name: k.name,
    key_prefix: k.key_prefix,
    scopes: k.scopes,
    created_at: k.created_at,
    last_used_at: k.last_used_at,
    expires_at: k.expires_at,
  }));
  return NextResponse.json({ api_keys: keys });
}

export async function POST(req: NextRequest): Promise<NextResponse> {
  const ctx = await requireAuth(req);
  if (ctx instanceof NextResponse) return ctx;
  if (!requireRole(ctx, "admin")) return cloudErrorResponse("Insufficient permissions", 403);

  const body = await req.json().catch(() => ({}));
  const parsed = parseBody(CreateApiKeySchema, body);
  if ("errors" in parsed)
    return cloudErrorResponse(parsed.errors.issues[0]?.message ?? "Invalid input", 400);

  const { key, rawKey } = createApiKey(
    ctx.tenantId,
    ctx.userId,
    parsed.data.name,
    parsed.data.scopes,
  );
  auditLog(ctx, "api_key.create", "api_key", key.id, { name: key.name }, req);
  return NextResponse.json(
    {
      api_key: {
        id: key.id,
        name: key.name,
        key_prefix: key.key_prefix,
        scopes: key.scopes,
        created_at: key.created_at,
      },
      raw_key: rawKey, // shown once
    },
    { status: 201 },
  );
}

import { NextRequest, NextResponse } from "next/server";
import { requireAuth, cloudErrorResponse } from "@/lib/cloud-auth";
import { listAuditEvents } from "@/lib/cloud-db";

export const runtime = "nodejs";

export async function GET(req: NextRequest): Promise<NextResponse> {
  const ctx = await requireAuth(req);
  if (ctx instanceof NextResponse) return ctx;

  const limit = Math.min(parseInt(req.nextUrl.searchParams.get("limit") ?? "100", 10), 500);
  const offset = parseInt(req.nextUrl.searchParams.get("offset") ?? "0", 10);

  const events = listAuditEvents(ctx.tenantId, limit, offset);
  return NextResponse.json({ events, limit, offset });
}

import { NextRequest, NextResponse } from "next/server";
import { requireAuth, cloudErrorResponse } from "@/lib/cloud-auth";
import { getEntitlement, checkRunLimit } from "@/lib/cloud-db";

export const runtime = "nodejs";

export async function GET(req: NextRequest): Promise<NextResponse> {
  const ctx = await requireAuth(req);
  if (ctx instanceof NextResponse) return ctx;

  const ent = getEntitlement(ctx.tenantId);
  const limits = checkRunLimit(ctx.tenantId);

  return NextResponse.json({
    plan: ent?.plan ?? "free",
    status: ent?.status ?? "active",
    stripe_customer_id: ent?.stripe_customer_id ?? null,
    has_active_subscription: !!(ent?.stripe_subscription_id && ent?.status === "active"),
    usage: {
      runs_used: limits.used,
      runs_limit: limits.limit,
      runs_remaining: limits.limit === -1 ? null : Math.max(0, limits.limit - limits.used),
    },
    limits: {
      runs_per_month: ent?.runs_per_month ?? 100,
      pack_limit: ent?.pack_limit ?? 5,
      retention_days: ent?.retention_days ?? 7,
    },
    period_start: ent?.period_start ?? null,
    period_end: ent?.period_end ?? null,
  });
}

/**
 * Dev-only seed endpoint.
 * POST /api/v1/seed
 * Creates a dev tenant + user + sample workflow + sample packs.
 * DISABLED in production.
 */
import { NextRequest, NextResponse } from "next/server";
import {
  seedDevData,
  createPack,
  publishPackVersion,
  createProject,
  getTenantBySlug,
  getUserByEmail,
} from "@/lib/cloud-db";

export const runtime = "nodejs";

export async function POST(_req: NextRequest): Promise<NextResponse> {
  if (process.env.NODE_ENV === "production") {
    return NextResponse.json({ error: "Seed endpoint disabled in production" }, { status: 403 });
  }
  if (process.env.REACH_CLOUD_ENABLED !== "true") {
    return NextResponse.json({ error: "REACH_CLOUD_ENABLED not set" }, { status: 503 });
  }

  try {
    const { tenant, user, rawApiKey } = seedDevData();

    // Seed sample marketplace packs
    const SAMPLE_PACKS = [
      {
        name: "Web Research Agent",
        slug: "web-research-agent",
        description: "Automated web research with intelligent citations and source verification.",
        shortDescription: "Automated web research with citations",
        category: "research" as const,
        visibility: "public" as const,
        tools: ["http.get", "browser.navigate", "search.query"],
        tags: ["web", "research", "citations"],
        permissions: ["network:external", "browser:read"],
        dataHandling: "minimal" as const,
        authorName: "ReadyLayer Team",
        version: "2.1.0",
        readme:
          "# Web Research Agent\n\nAutomated web research with intelligent citations.\n\n## Features\n- Multi-source research\n- Citation generation",
        changelog: "Initial release",
        reputationScore: 98,
        downloads: 15420,
      },
      {
        name: "Data Analysis Pack",
        slug: "data-analysis-pack",
        description: "Comprehensive CSV/Excel analysis with automated chart generation.",
        shortDescription: "CSV/Excel analysis with charts",
        category: "data" as const,
        visibility: "public" as const,
        tools: ["file.read", "data.process", "chart.generate"],
        tags: ["csv", "excel", "analytics"],
        permissions: ["fs:read", "memory:high"],
        dataHandling: "processed" as const,
        authorName: "DataFlow Inc",
        version: "3.2.1",
        readme: "# Data Analysis Pack\n\nAnalyze your data with AI-powered insights.",
        changelog: "Fixed Excel parsing",
        reputationScore: 94,
        downloads: 8930,
      },
      {
        name: "Code Review Agent",
        slug: "code-review-agent",
        description:
          "Automated PR review with security scanning and best practice recommendations.",
        shortDescription: "Automated PR review and security scanning",
        category: "development" as const,
        visibility: "public" as const,
        tools: ["git.read", "code.analyze", "security.scan"],
        tags: ["code-review", "security", "devops"],
        permissions: ["git:read", "network:external"],
        dataHandling: "minimal" as const,
        authorName: "DevTools Co",
        version: "4.0.2",
        readme: "# Code Review Agent\n\nAI-powered code review for modern development teams.",
        changelog: "Added multi-language support",
        reputationScore: 96,
        downloads: 12300,
      },
    ];

    for (const p of SAMPLE_PACKS) {
      try {
        const pack = createPack(tenant.id, user.id, p);
        publishPackVersion(
          pack.id,
          p.version,
          JSON.stringify({ name: p.name, version: p.version, tools: p.tools }),
          p.readme,
          p.changelog,
        );
        // Manually set reputation/downloads
        // @ts-ignore
        const { default: Database } = await import("better-sqlite3");
        const path = await import("path");
        const dbPath = process.env.CLOUD_DB_PATH ?? path.join(process.cwd(), "reach-cloud.db");
        const db = new Database(dbPath);
        db.prepare(
          "UPDATE packs SET reputation_score=?, downloads=?, security_status=?, verified=1 WHERE id=?",
        ).run(p.reputationScore, p.downloads, "passed", pack.id);
        db.close();
      } catch {
        /* already seeded */
      }
    }

    return NextResponse.json({
      ok: true,
      tenant: { id: tenant.id, slug: tenant.slug, name: tenant.name },
      user: { id: user.id, email: user.email },
      api_key: rawApiKey === "ALREADY_SEEDED" ? "[already seeded - check existing key]" : rawApiKey,
      note:
        rawApiKey === "ALREADY_SEEDED"
          ? "Tenant already seeded."
          : "Dev seed created. Store your API key ΓÇö shown only once.",
    });
  } catch (err) {
    console.error("[seed]", err);
    return NextResponse.json({ error: String(err) }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from "next/server";
import { requireAuth, cloudErrorResponse, auditLog } from "@/lib/cloud-auth";
import {
  createTenant,
  getTenantBySlug,
  addMember,
  listTenantsForUser,
  getEntitlement,
} from "@/lib/cloud-db";
import { CreateTenantSchema, parseBody } from "@/lib/cloud-schemas";

export const runtime = "nodejs";

export async function GET(req: NextRequest): Promise<NextResponse> {
  const ctx = await requireAuth(req);
  if (ctx instanceof NextResponse) return ctx;
  const tenants = listTenantsForUser(ctx.userId);
  return NextResponse.json({ tenants });
}

export async function POST(req: NextRequest): Promise<NextResponse> {
  const ctx = await requireAuth(req);
  if (ctx instanceof NextResponse) return ctx;

  const body = await req.json().catch(() => ({}));
  const parsed = parseBody(CreateTenantSchema, body);
  if ("errors" in parsed)
    return cloudErrorResponse(parsed.errors.issues[0]?.message ?? "Invalid input", 400);

  if (getTenantBySlug(parsed.data.slug))
    return cloudErrorResponse("Tenant slug already taken", 409);

  const tenant = createTenant(parsed.data.name, parsed.data.slug);
  addMember(tenant.id, ctx.userId, "owner");
  auditLog(
    ctx,
    "tenant.create",
    "tenant",
    tenant.id,
    { name: tenant.name, slug: tenant.slug },
    req,
  );
  return NextResponse.json({ tenant }, { status: 201 });
}

import { NextRequest, NextResponse } from "next/server";
import { requireAuth, cloudErrorResponse } from "@/lib/cloud-auth";
import { listWorkflowRuns } from "@/lib/cloud-db";

export const runtime = "nodejs";

export async function GET(req: NextRequest): Promise<NextResponse> {
  const ctx = await requireAuth(req);
  if (ctx instanceof NextResponse) return ctx;
  const limit = parseInt(req.nextUrl.searchParams.get("limit") ?? "50", 10);
  const runs = listWorkflowRuns(ctx.tenantId, undefined, Math.min(limit, 200));
  return NextResponse.json({ runs });
}

"use client";

import { useState } from "react";
import Link from "next/link";
import { useRouter } from "next/navigation";
import { track } from "@/lib/analytics";
import { ROUTES } from "@/lib/routes";
import { CTA } from "@/lib/copy";

export default function CloudRegisterPage() {
  const [email, setEmail] = useState("");
  const [error, setError] = useState("");
  const [info, setInfo] = useState("");
  const [loading, setLoading] = useState(false);
  const router = useRouter();

  function githubSignUp() {
    track("signup_started", {
      method: "github_oauth",
      source: "register_page",
    });
    window.location.href = `${ROUTES.API.V1.AUTH.GITHUB}?next=/dashboard`;
  }

  async function handleMagicLink(e: React.FormEvent) {
    e.preventDefault();
    setLoading(true);
    setError("");
    setInfo("");
    try {
      await track("signup_started", {
        method: "magic_link",
        source: "register_page",
      });
      const res = await fetch(ROUTES.API.V1.AUTH.MAGIC_LINK, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email }),
      });
      const data = (await res.json()) as {
        ok?: boolean;
        error?: string;
        dev_link?: string;
      };
      if (!res.ok) {
        setError(data.error ?? "Could not send link");
        return;
      }
      setInfo("Check your email ΓÇö we sent you a sign-in link.");
      if (data.dev_link) setInfo(`Dev mode link: ${data.dev_link}`);
    } catch {
      setError("Network error. Please try again.");
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-background px-4">
      <div className="w-full max-w-md">
        {/* Logo */}
        <div className="text-center mb-8">
          <Link href="/" className="inline-flex items-center gap-2 mb-6">
            <div className="w-8 h-8 rounded-lg flex items-center justify-center logo-gradient">
              <span className="text-white font-bold text-lg">R</span>
            </div>
          </Link>
          <h1 className="text-2xl font-bold text-white">Create your free account</h1>
          <p className="text-gray-400 mt-1 text-sm">{CTA.reassurance}</p>
        </div>

        <div className="card p-8">
          {error && (
            <div className="mb-4 p-3 rounded-lg bg-red-500/10 border border-red-500/30 text-red-400 text-sm">
              {error}
            </div>
          )}
          {info && (
            <div className="mb-4 p-3 rounded-lg bg-emerald-500/10 border border-emerald-500/30 text-emerald-400 text-sm break-all">
              {info}
            </div>
          )}

          {/* GitHub ΓÇö primary fastest path */}
          <button
            type="button"
            onClick={githubSignUp}
            className="w-full flex items-center justify-center gap-3 py-3 px-4 rounded-lg border border-border bg-surface hover:bg-white/5 transition-colors text-sm font-medium mb-2"
          >
            <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24" aria-hidden="true">
              <path
                fillRule="evenodd"
                d="M12 2C6.477 2 2 6.484 2 12.017c0 4.425 2.865 8.18 6.839 9.504.5.092.682-.217.682-.483 0-.237-.008-.868-.013-1.703-2.782.605-3.369-1.343-3.369-1.343-.454-1.158-1.11-1.466-1.11-1.466-.908-.62.069-.608.069-.608 1.003.07 1.531 1.032 1.531 1.032.892 1.53 2.341 1.088 2.91.832.092-.647.35-1.088.636-1.338-2.22-.253-4.555-1.113-4.555-4.951 0-1.093.39-1.988 1.029-2.688-.103-.253-.446-1.272.098-2.65 0 0 .84-.27 2.75 1.026A9.564 9.564 0 0112 6.844c.85.004 1.705.115 2.504.337 1.909-1.296 2.747-1.027 2.747-1.027.546 1.379.202 2.398.1 2.651.64.7 1.028 1.595 1.028 2.688 0 3.848-2.339 4.695-4.566 4.943.359.309.678.92.678 1.855 0 1.338-.012 2.419-.012 2.747 0 .268.18.58.688.482A10.019 10.019 0 0022 12.017C22 6.484 17.522 2 12 2z"
                clipRule="evenodd"
              />
            </svg>
            Continue with GitHub
          </button>
          <p className="text-xs text-gray-600 text-center mb-4">
            Fastest ΓÇö imports your identity automatically
          </p>

          <div className="relative mb-4">
            <div className="absolute inset-0 flex items-center">
              <div className="w-full border-t border-border" />
            </div>
            <div className="relative flex justify-center text-xs text-gray-500">
              <span className="bg-surface px-2">or use your email</span>
            </div>
          </div>

          {/* Magic link form */}
          <form onSubmit={handleMagicLink} className="space-y-3">
            <div>
              <label className="block text-sm text-gray-400 mb-1">Work email</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                autoFocus
                className="w-full px-3 py-2 bg-background border border-border rounded-lg text-white placeholder-gray-500 focus:outline-none focus:border-accent"
                placeholder="you@yourcompany.com"
              />
            </div>
            <button
              type="submit"
              disabled={loading}
              className="w-full py-2 px-4 bg-accent text-white font-semibold rounded-lg hover:bg-accent/90 disabled:opacity-50 transition-colors"
            >
              {loading ? "Sending linkΓÇª" : "Continue with email"}
            </button>
          </form>

          <p className="mt-6 text-center text-sm text-gray-500">
            Already have an account?{" "}
            <Link href="/cloud/login" className="text-accent hover:underline">
              Sign in
