import { useState, useEffect } from "react";
import Link from "next/link";
import { ROUTES } from "@/lib/routes";

interface Signal {
  id: string;
  name: string;
  type: string;
  source: string;
  status: "enabled" | "disabled";
  created_at: string;
}
interface MonitorRun {
  id: string;
  signal_id: string;
  value: number;
  alert_triggered: boolean;
  created_at: string;
}
interface Health {
  total: number;
  alerts_today: number;
  latest_drift: number;
}

function MetricCard({
  label,
  value,
  sub,
  alert,
}: {
  label: string;
  value: string | number;
  sub?: string;
  alert?: boolean;
}) {
  return (
    <div
      className={`p-4 rounded-xl border ${alert ? "border-orange-500/30 bg-orange-500/5" : "border-border bg-surface"}`}
    >
      <p className="text-xs text-gray-400 mb-1">{label}</p>
      <p className={`text-2xl font-bold ${alert ? "text-orange-400" : "text-white"}`}>{value}</p>
      {sub && <p className="text-xs text-gray-500 mt-1">{sub}</p>}
    </div>
  );
}

function Sparkline({ values }: { values: number[] }) {
  if (values.length < 2) return <div className="h-8 text-xs text-gray-500">No data</div>;
  const max = Math.max(...values, 1);
  const min = Math.min(...values, 0);
  const range = max - min || 1;
  const points = values
    .map((v, i) => {
      const x = (i / (values.length - 1)) * 100;
      const y = 100 - ((v - min) / range) * 100;
      return `${x},${y}`;
    })
    .join(" ");
  return (
    <svg viewBox="0 0 100 100" preserveAspectRatio="none" className="h-8 w-full">
      <polyline
        fill="none"
        stroke="currentColor"
        strokeWidth="2"
        points={points}
        className="text-accent"
      />
    </svg>
  );
}

export default function MonitoringPage() {
  const [signals, setSignals] = useState<Signal[]>([]);
  const [health] = useState<Health>({
    total: 0,
    alerts_today: 0,
    latest_drift: 0,
  });
  const [loading, setLoading] = useState(true);
  const [showCreate, setShowCreate] = useState(false);
  const [form, setForm] = useState({
    name: "",
    type: "drift",
    source: "webhook",
  });
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    Promise.all([fetch("/api/v1/signals").then((r) => r.json())])
      .then(([sigData]) => {
        setSignals(sigData.signals ?? []);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, []);

  async function handleCreateSignal(e: React.FormEvent) {
    e.preventDefault();
    setSaving(true);
    const res = await fetch("/api/v1/signals", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });
    if (res.ok) {
      const data = (await res.json()) as { signal: Signal };
      setSignals((prev) => [data.signal, ...prev]);
      setShowCreate(false);
    }
    setSaving(false);
  }

  async function toggleSignal(signal: Signal) {
    const next = signal.status === "enabled" ? "disabled" : "enabled";
    const res = await fetch(`/api/v1/signals/${signal.id}`, {
      method: "PATCH",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ status: next }),
    });
    if (res.ok)
      setSignals((prev) => prev.map((s) => (s.id === signal.id ? { ...s, status: next } : s)));
  }

  const driftLabel =
    health.latest_drift > 0.5 ? "ΓÜá High" : health.latest_drift > 0.2 ? "Moderate" : "Stable";

  return (
    <div className="min-h-screen bg-background py-8 px-6">
