/**
 * Supported programming languages.
 */
export type SupportedLanguage =
  | "javascript"
  | "typescript"
  | "python"
  | "go"
  | "rust"
  | "java"
  | "csharp"
  | "ruby"
  | "php"
  | "unknown";

// ΓöÇΓöÇ Language Detection ΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇΓöÇ

/**
 * Detects the primary language of a repository.
 */
export function detectLanguage(repoPath: string): {
  language: SupportedLanguage;
  confidence: number;
  files: string[];
} {
  const extensions = new Map<string, number>();

  // Count file extensions
  const scanDir = (dir: string): string[] => {
    const files: string[] = [];
    try {
      const entries = require("fs").readdirSync(dir);
      for (const entry of entries) {
        const fullPath = join(dir, entry);
        const stat = require("fs").statSync(fullPath);

        if (stat.isDirectory()) {
          if (!entry.startsWith(".") && entry !== "node_modules" && entry !== "dist") {
            files.push(...scanDir(fullPath));
          }
        } else {
          const ext = extname(entry);
          if (ext) {
            files.push(ext);
            extensions.set(ext, (extensions.get(ext) || 0) + 1);
          }
        }
      }
    } catch {
      // Ignore permission errors
    }
    return files;
  };

  const files = scanDir(repoPath);

  // Map extensions to languages
  const langMap: Record<string, SupportedLanguage> = {
    ".js": "javascript",
    ".jsx": "javascript",
    ".ts": "typescript",
    ".tsx": "typescript",
    ".py": "python",
    ".go": "go",
    ".rs": "rust",
    ".java": "java",
    ".cs": "csharp",
    ".rb": "ruby",
    ".php": "php",
  };

  const langCounts: Record<SupportedLanguage, number> = {
    javascript: 0,
    typescript: 0,
    python: 0,
    go: 0,
    rust: 0,
    java: 0,
    csharp: 0,
    ruby: 0,
    php: 0,
    unknown: 0,
  };

  for (const ext of files) {
    const lang = langMap[ext];
    if (lang) {
      langCounts[lang]++;
    }
  }

  // Find dominant language
  let maxCount = 0;
  let dominantLang: SupportedLanguage = "unknown";

  for (const [lang, count] of Object.entries(langCounts)) {
    if (count > maxCount) {
      maxCount = count;
      dominantLang = lang as SupportedLanguage;
    }
  }

  const total = files.length;
  const confidence = total > 0 ? maxCount / total : 0;

  return {
    language: dominantLang,
    confidence,
    files,
  };
}

